const steps = ["Receive Address", "Save Txn Info"];

function getStepContent(step) {
  switch (step) {
    case 0:
      return <Exchange1 />;
    case 1:
      return <Exchange2 />;
    default:
      throw new Error("Unknown step");
  }
}



